mapper.update:
update user


        email = #{email}


        phone = #{phone}


        password = #{password}


        avatar = #{avatar}


        gender = #{gender}


        nickname = #{nickname}


        description = #{description}


        state = #{state}


        default_favorite = #{defaultFavorite}


where id = #{id}

mapper.select:
select * from user


        id = #{id}


        email = #{email}


        phone = #{phone}


        nickname = #{nickname}


        gender = #{gender}


        description = #{description}


        avatar = #{avatar}


        state = #{state}


        default_favorite = #{defaultFavorite}


        password = #{password}

select * from user where id in

    #{id}

ORDER BY FIELD(id,

    #{id}
